&lt;span style=&quot;color: #aaffaa; font-weight: bold;&quot;&gt;+&lt;/span&gt;&lt;span style=&quot;color: #aaffaa; font-weight: bold;&quot;&gt;dir=&quot;$( cd &quot;$( dirname &quot;${BASH_SOURCE[0]}&quot; )&quot; &amp;amp;&amp;amp; pwd )&quot;&lt;/span&gt;
