                       &lt;span style=&quot;color: #ccaaff;&quot;&gt;:steps&lt;/span&gt; &lt;span style=&quot;color: #aaccff;&quot;&gt;[&lt;/span&gt;,,,&lt;span style=&quot;color: #aaccff;&quot;&gt;]&lt;/span&gt;
                       &lt;span style=&quot;color: #ccaaff;&quot;&gt;:cooking-time&lt;/span&gt; 30&lt;span style=&quot;color: #81d4fa;&quot;&gt;}&lt;/span&gt;&lt;span style=&quot;color: #aadddd;&quot;&gt;)&lt;/span&gt;

&lt;span style=&quot;color: #aadddd;&quot;&gt;(&lt;/span&gt;&lt;span style=&quot;color: #aaffaa;&quot;&gt;let&lt;/span&gt; &lt;span style=&quot;color: #81d4fa;&quot;&gt;[&lt;/span&gt;&lt;span style=&quot;color: #aaccff;&quot;&gt;{&lt;/span&gt;&lt;span style=&quot;color: #ccaaff;&quot;&gt;:&lt;/span&gt;&lt;span style=&quot;color: #fff59d;&quot;&gt;robochef&lt;/span&gt;&lt;span style=&quot;color: #000000; background-color: #ffffff;&quot;&gt;/&lt;/span&gt;&lt;span style=&quot;color: #ccaaff;&quot;&gt;keys&lt;/span&gt; &lt;span style=&quot;color: #aaeecc;&quot;&gt;[&lt;/span&gt;steps serves&lt;span style=&quot;color: #aaeecc;&quot;&gt;]&lt;/span&gt;&lt;span style=&quot;color: #aaccff;&quot;&gt;}&lt;/span&gt; recipe&lt;span style=&quot;color: #81d4fa;&quot;&gt;]&lt;/span&gt;
  &lt;span style=&quot;color: #81d4fa;&quot;&gt;(&lt;/span&gt;&lt;span style=&quot;color: #aaffaa;&quot;&gt;doseq&lt;/span&gt; &lt;span style=&quot;color: #aaccff;&quot;&gt;[&lt;/span&gt;s steps&lt;span style=&quot;color: #aaccff;&quot;&gt;]&lt;/span&gt;
    ,,,&lt;span style=&quot;color: #81d4fa;&quot;&gt;)&lt;/span&gt;&lt;span style=&quot;color: #aadddd;&quot;&gt;)&lt;/span&gt;
